import React, { Component } from 'react'
import { Switch, Route } from "react-router-dom";
import Home from "./components/Home";
import DetailsAlbum from "./components/DetailsAlbum";
import FavoritesAlbums from "./components/FavoritesAlbums";
import { Signin } from "./components/auth/Signin";
import { Signup } from "./components/auth/Signup";
import Forgot from "./components/auth/Forgot";
import Reset from "./components/auth/Reset";


export default class Routes extends Component {
    state = {
        user: null,
        isSignin: false,
      };
      componentDidMount() {
        this.userSignin();
      }
      userSignin = () => {
        if (localStorage.token) {
          let token = localStorage.token;
          let user = jwt_decode(token, "SECRET").user;
          this.setState({
            user: user,
            isLogin: true,
          });
        } else {
          this.setState({
            user: null,
            isLogin: false,
          });
        }
      };
    render(){
  return (
    <Switch>
      <Route
        path="/signin"
        render={(props) => <Signin {...props} userSignin={this.userSignin} />}
      />
      <Route path="/signin" component={Signin} />
      <Route path="/forgot" component={Forgot} />
      <Route
        exact
        path="/reset/:token"
        render={(props) => <Reset {...props} />}
      />
      <Route path="/signup" component={Signup} />

      <Route path="/" exact component={Home} />
      <Route path="/details/:id" exact component={DetailsAlbum} />
      <Route path="/favorites" exact component={FavoritesAlbums} />
    </Switch>
  );
    }
};
