import React, { Component } from 'react'
import * as actions from './action/index'
import Header from './widgets/Header'


export default class DetailsAlbum extends Component {

    state = {
        album: '',
        tracks: []
    }

    componentDidMount(){
        actions.getAlbum(this.props.match.params.id).then(album =>{
            this.setState({
                album,
                tracks: album.tracks.data
            })
        })
    }

    renderTracks = () => {
        const {tracks} = this.state;
        return tracks && tracks.length ?
            tracks.map((track,index) => (
                <figure key={index}>
                    <figcaption>Listen {track.title}</figcaption>
                    <audio
                        controls
                        src={track.preview}>
                            Your browser does not support
                            <code>audio</code> elemens.
                    </audio>
                </figure>
            ))
        :
        null;
    }

    renderAlbum = () => {
        const {album} = this.state;
        return  (
            <div className="col md-12 mb-3">
                <div className="card border-dark">
                    <img src={album.cover_big} alt="" className="card-img-top"/>
                    <div className="card-body">
                    <span className="text-primary">{album.release_date} </span>
                            <div className="card-title">
                                            {album.title}
                            </div>
                    </div>
                    <div className="card-footer">
                        {this.renderTracks()}

                    </div>   
                </div>
            </div>)


    }


    render() {
        console.log(this.state);
        return (
            <div className="container">
                <div class="row mt-4">
                    <div className="col-md-10 mx-auto">
                        <div className="row">
                            {this.renderAlbum()}

                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
